INSERT INTO `ires_exam_title` VALUES ('262', '1', '2', '8', '<p>商场改革经营管理办法后，本月盈利比上月盈利的2倍还多12万元，又知本月盈利比上月盈利多30万元，求这两个月盈利各是多少万元？</p>', '', '上月盈利＝（30－12）÷（2－1）＝18（万元）\r\n本月盈利＝18＋30＝48（万元）', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '3', '0');
