INSERT INTO `ires_exam_title` VALUES ('685', '1', '3', '3', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; (A) &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Mr. Green is an Englishman. He is a teacher. He teaches English in No.15 Middle School in Beijing. He likes teaching a lot. He goes to work by school bus every morning</p>\r\n<p>&nbsp; &nbsp; &nbsp;There are fifty students in his class. He speaks English with them in class. He plays games with them after class. He likes them very much. And his students love&nbsp;him, too. They are good friends.&nbsp;</p>\r\n<p>判断下列句子与短文内容是否相符，</p>\r\n<p>(&nbsp;&nbsp;)1.&nbsp;Mr.&nbsp;Green&nbsp;is&nbsp;Canadian.</p>\r\n<p>(&nbsp;&nbsp;)2.&nbsp;He&nbsp;likes&nbsp;teaching&nbsp;very&nbsp;much.&nbsp;</p>\r\n<p>(&nbsp;&nbsp;)3.&nbsp;He&nbsp;goes&nbsp;to&nbsp;work&nbsp;by&nbsp;bike&nbsp;every&nbsp;morning.</p>\r\n<p>(&nbsp;&nbsp;)4.&nbsp;He&nbsp;speaks&nbsp;Chinese&nbsp;in&nbsp;class.&nbsp;</p>\r\n<p>(&nbsp;&nbsp;)5.&nbsp;He&nbsp;likes&nbsp;his&nbsp;students&nbsp;very&nbsp;much.&nbsp;</p>', '正确,错误', 'b,a,b,b,a', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '3', '0');
INSERT INTO `ires_exam_title` VALUES ('686', '1', '3', '7', '<p><!--StartFragment --></p>\r\n<div><img src=\"file:///C:\\Users\\yan\\AppData\\Local\\Temp\\MNZG)S@Y~O64{]B~UL$X2KN.gif\" alt=\"\" />&nbsp; &nbsp; &nbsp;This is a picture of a family. The grandmother&rsquo;s name is Harry Smith. The grandfather&rsquo;s name is Jean Smith. They are very old. They are Americans. The father&rsquo;s name is Lake Smith. He is 44. The mother&rsquo;s name is Kate Smith. She is 40. They have a son of ten and a daughter of fifteen. The son&rsquo;s name is John Smith and the daughter&rsquo;s mane is Mary Smith. They are students of No.12 Middle School. Kate is a teacher of the same school. Lake is a policeman. It&rsquo;s a very good family.&nbsp;</div>\r\n<div>(&nbsp;&nbsp;)1.&nbsp;Lake&nbsp;Smith&rsquo;s&nbsp;parents&nbsp;are&nbsp;_______________. &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; A.&nbsp;Americans&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;B.&nbsp;American&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C. America</div>\r\n<div>(&nbsp;&nbsp;)2.&nbsp;John&rsquo;s&nbsp;father&nbsp;is&nbsp;______________.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;A.&nbsp;44&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;B.&nbsp;40&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C.&nbsp;very&nbsp;old</div>\r\n<div>(&nbsp;&nbsp;)3.&nbsp;Mary&rsquo;s&nbsp;mother&nbsp;is&nbsp;a&nbsp;_____________.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;A.&nbsp;teacher&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;B.&nbsp;policeman&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C.&nbsp;doctor&nbsp;&nbsp;&nbsp;</div>\r\n<div>(&nbsp;&nbsp;)4.&nbsp;They&nbsp;are&nbsp;in&nbsp;the&nbsp;___________school.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</div>\r\n<div>&nbsp; &nbsp; &nbsp; A.&nbsp;some&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;B.&nbsp;same&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C.&nbsp;any&nbsp;&nbsp;</div>\r\n<div>(&nbsp;&nbsp;)5.&nbsp;The&nbsp;family&nbsp;has&nbsp;_____________children.&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;A.&nbsp;six&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;B.&nbsp;two&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;C.&nbsp;four</div>', '', 'A,A,A,B,B', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '3', '0');
