INSERT INTO `ires_exam_title` VALUES ('701', '1', '1', '1', '<p>按要求朗读，重音标示错误的一组是（ &nbsp;&nbsp;）</p>', '今年我上五年级。（不是明年）,吴昊喜欢踢足球。（不讨厌踢足球）,南京是江苏省的省会（其它的城市不是）,这是我的语文书（不是别人的）', 'a', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '1', '0');
