INSERT INTO `ires_exam_title` VALUES ('716', '1', '1', '1', '<p>下列句子不属于比喻句的是（&nbsp;&nbsp;&nbsp; ）</p>', '落在电线上的燕子，就像是五线谱上的音符。,秋风如同柔韧的梳子，把田野梳得井井有条。,小兴安岭是一座巨大的宝库。,他的身子还是那么单薄，像是生病的样子。', 'd', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '3', '0');
