INSERT INTO `ires_exam_title` VALUES ('726', '1', '1', '1', '<p>《尼尔斯骑鹅旅行记》作为9至13岁孩子的通俗的历史和地理读物，具有很强的趣味性。其中，风、河流、城市都成了拟人化的形象，巧妙的比喻更让人耳目一新，如把斯康奈的平坦大地比喻为(&nbsp; &nbsp; )。</p>', '“方格子布”,“粗麻布\",“长面包和小点心”,“黑色的线穗”', 'a', '7', '丁岩', '1', '0', '2', '0', '0', '1000', '3', '0');
