INSERT INTO `ires_exam_title` VALUES ('1152', '1', '2', '8', '<p>敬老院有18位老奶奶,平均年龄是75岁。有12位老爷爷,平均年龄是70岁。这些老人的平均年龄是多少岁?</p>', '', '(75×18+70×12)÷(18+12)=73(岁)', '10', '', '1', '0', '1', '0', '0', '1000', '3', '0');
