INSERT INTO `ires_exam_title` VALUES ('1189', '1', '2', '3', '<p>桌子上有15张卡片,分别写着数字1~15,背面朝上,从中任意摸一张,如果摸到单数,小丽赢,如果摸到双数,小明赢。这样约定很公平。&nbsp; (　　)</p>', '正确,错误', 'b', '10', '', '1', '0', '1', '0', '0', '1000', '3', '0');
