INSERT INTO `ires_exam_title` VALUES ('1193', '1', '1', '3', '<p>&ldquo;一往情深、一技之长、一丝不苟、一模一样&rdquo;的&ldquo;一&rdquo;字正确读音是：yǐ y&iacute; yǐ yǐ y&iacute;. &nbsp;（ &nbsp; &nbsp;）</p>', '正确,错误', 'b', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
INSERT INTO `ires_exam_title` VALUES ('1194', '1', '1', '3', '<p>《学弈》这篇文言文选自《孟子﹒汤问》是写了弈秋教两个人下棋，一人专心致志，另一个人三心二意。 &nbsp;（ &nbsp; ）</p>', '正确,错误', 'b', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
INSERT INTO `ires_exam_title` VALUES ('1195', '1', '1', '3', '<p>《匆匆》一文第一自然段第一句排比句描绘了春景的美。 &nbsp;（ &nbsp; &nbsp;）</p>', '正确,错误', 'b', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
