INSERT INTO `ires_exam_title` VALUES ('1199', '1', '1', '3', '<p>《顶碗少年》的作者是赵丽宏，而《手指》的作者是丰子恺。 &nbsp;（ &nbsp; &nbsp;）</p>', '正确,错误', 'a', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
INSERT INTO `ires_exam_title` VALUES ('1200', '1', '1', '3', '<p>我读了《顶碗少年》和《手指》这两篇文章我悟出人生的哲理和团结就是力量的道理。 &nbsp;（ &nbsp; &nbsp;）</p>', '正确,错误', 'a', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
INSERT INTO `ires_exam_title` VALUES ('1201', '1', '1', '3', '<p>《江畔独步寻花》是一首描写春景的诗。 &nbsp; （ &nbsp; &nbsp; ）</p>', '正确,错误', 'b', '7', '', '1', '0', '2', '0', '0', '1000', '3', '0');
